import React, { Component } from 'react';
import './Tree.css';

export class Node extends Component {
  render() {
    return (<div>div</div>);
  }
}

class Tree extends Component {
  subtree(nodes) {
    let obj = [];
    React.Children.map(nodes, (node) => {
      if(node.props.children) {
        obj.push(
          <li>
            <a>{node.props.label || 'node'}</a>
            {this.subtree(node.props.children)}
          </li>
        );
      } else {
        obj.push(<li><a>{node.props.label || 'node'}</a></li>);
      }
    });
    return (<ul>{obj}</ul>);
  }
  render() {
    return (
      <div className="container">
        <div className="tree">
          <ul>
            <li>
              <a>{'root' || this.props.root}</a>
              {this.subtree(this.props.children)}
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

Tree.Node = Node;

export default Tree;
